router.post("/admin-login", (req,res) => {
  // console.log(req.body)
  productHelper.doLogin(req.body).then((response) => {
    //console.log(response)    
    if(response.status){                    //ee responseil admin data und
      
      req.session.adminLoggedIn = true
      req.session.admin = response.admin      // ee response.user =  user      und,  "doLogin" enna function define cheyyumbol  avide "response" enna objectinte ullil  "response.user = user"  and "response.status = true"  ennum kodthirunnu
      console.log(req.session.admin)
      res.redirect("/admin")
    }else{
      req.session.adminLoggErr = true;
      res.redirect("/admin-login")
      
    }
  })
})

